DROP TABLE recipes, users;

CREATE TABLE users (
id serial primary key,
name text,
email text,
password text
);


CREATE TABLE recipes (
id serial primary key,
title text,
image text,
description text,
user_id integer,
constraint fk_user foreign key(user_id) references users(id)
);

